local TCheckButton = {}

local Types = {
	Default = {
		ActiveIcon = "",
		InactiveIcon = "",
		LockedIcon = ""
	},
	Switch = {
		InactiveIcon = "",
		ActiveIcon = "",
		LockedIcon = [[]]
	},
	YesNo = {
		ActiveIcon = [[]],
		InactiveIcon = [[]],
		LockedIcon = [[]]
	},
	Button = {
		ActiveIcon = [[]],
		InactiveIcon = [[]],
		LockedIcon = [[]],
	},
}
local function SetIcon(self, state)
	if not self.Enabled then
		self.Picture:SetPicture(self.LockedIcon:decode("base64"),true)
	elseif state then
		self.Picture:SetPicture(self.ActiveIcon:decode("base64"),true)
	else
		self.Picture:SetPicture(self.InactiveIcon:decode("base64"),true)	
	end
end

local function SetType(self)
	if not Types[self.Type] then
		self.Type = "Default"
	end
	self.ActiveIcon = Types[self.Type].ActiveIcon or ""
	self.InactiveIcon = Types[self.Type].InactiveIcon or ""
	self.LockedIcon = Types[self.Type].LockedIcon or ""
end

local function SetState(self, state)
	self.Check = state
	SetIcon(self,state)
	if type(self.OnCheckStateChanged) == "function" then
		assert(self.OnCheckStateChanged)(self.inst)
	end
end

local function CreateObject(self)
	local obj = {}
	local m = {
		["__index"] = function(t,idx)
			if idx == "Site" or idx == "Picture" then
				return nil
			end
			return self[idx]
		end,
		["__newindex"] = function(t,idx,val)
			if idx == "Check" then
				self.Check = val
				SetState(self,val)
			elseif idx == "Enabled" then
				self.Enabled = val
				SetIcon(self,self.Check)
			elseif idx == "Type" then
				self.Type = val
				SetType(self)
			elseif idx == "X" or idx == "Y" or idx == "Width" or idx == "Height" then
				self[idx] = val
				self.Site[idx] = val
			elseif idx == "ActiveIcon" or idx == "InactiveIcon" or idx == "LockedIcon" then 	
				self[idx] = val
				SetIcon(self,self.Check)
			else
				self[idx] = val
			end
		end,
		["__metatable"] = "TCheckButton"
	}
	setmetatable(obj,m)
	return obj
end

local function new(site,icon1,icon2,icon3)
	local obj = {}
	obj.Site = site
	obj.Check = false
	obj.Enabled = true
	obj.Width = site.Width
	obj.Height = site.Height
	obj.X = 0
	obj.Y = 0
	obj.Type = "Default"
	if type(icon1) == "table" then
		obj.ActiveIcon = icon1[1] or ""
		obj.InactiveIcon = icon1[2] or ""
		obj.LockedIcon = icon1[3] or ""
	elseif type(icon1) == "string" and Types[icon1] then
		obj.ActiveIcon = Types[icon1].ActiveIcon or ""
		obj.InactiveIcon = Types[icon1].InactiveIcon or ""
		obj.LockedIcon = Types[icon1].LockedIcon or ""
		obj.Type = icon1
	elseif type(icon1) == "string" then	
		obj.ActiveIcon = icon1 or ""
		obj.InactiveIcon = icon2 or ""
		obj.LockedIcon = icon3 or ""
	else	
		obj.ActiveIcon = Types.Default.ActiveIcon or ""
		obj.InactiveIcon = Types.Default.InactiveIcon or ""
		obj.LockedIcon = Types.Default.LockedIcon or ""
	end	
	obj.Picture = PictureBox.new("pic","",0,0,site.Width,site.Height)
	obj.Site:AddControl(obj.Picture)
	obj.Picture.SizeMode = PictureBox.PictureAutoSize
	obj.Picture.OnClick = function()
		if not obj.Enabled then return end
		if type(obj.OnClick) == "function" then
			assert(obj.OnClick)(obj.inst)
		else
			obj.Check = not obj.Check
			SetState(obj,obj.Check)
		end	
	end
	SetIcon(obj)
	obj.inst = CreateObject(obj)
	return obj.inst
end

TCheckButton = {
	new = new,
	
}

setmetatable(TCheckButton, {["__call"] = function(self,...) return new(...) end } )

return TCheckButton
