local TCheckButton = require("TCheckButton")
local off_pic = ""
local on_pic = ""

function Форма_Load( form )
  checker = TCheckButton(Me.panel1,on_pic,off_pic)
  checker.OnCheckStateChanged = function(self)
  if self.Check then
    Me.textbox2.Text = "Вкл."   
  else
    Me.textbox2.Text = "Выкл."
  end	
end
